DROP TABLE IF EXISTS orderItems;
DROP TABLE IF EXISTS orders;
DROP TABLE IF EXISTS inventoryItems;
DROP TABLE IF EXISTS buyers;
DROP TABLE IF EXISTS sellers;
DROP TABLE IF EXISTS users;
DROP TABLE IF EXISTS books;

-- --- CREATE TABLES (Revised for 1:N Seller:Inventory) --- --


CREATE TABLE books (
    bookId BIGINT PRIMARY KEY,
    isbn VARCHAR(255),
    title VARCHAR(255) NOT NULL,
    grade INT,
    newPrice FLOAT,
    usedPrice FLOAT,
    costPrice FLOAT
);

CREATE TABLE users (
    userId BIGINT PRIMARY KEY,
    userUUID VARCHAR(255) NOT NULL UNIQUE,
    firstName VARCHAR(255) NOT NULL,
    lastName VARCHAR(255) NOT NULL,
    type TINYINT,
    email VARCHAR(255),
    cellphone VARCHAR(255),
    marketing TINYINT
);

CREATE TABLE buyers (
    buyerId BIGINT PRIMARY KEY,
    buyerUUID VARCHAR(255) NOT NULL UNIQUE,
    userId BIGINT NOT NULL UNIQUE,
    FOREIGN KEY (userId) REFERENCES users(userId) ON DELETE CASCADE ON UPDATE CASCADE
);

-- Sellers table links to Users (1:1 specialization)
CREATE TABLE sellers (
    sellerId BIGINT PRIMARY KEY,
    sellerUUID VARCHAR(255) NOT NULL UNIQUE,
    userId BIGINT NOT NULL UNIQUE,
    -- inventoryItemId FK removed from here
    FOREIGN KEY (userId) REFERENCES users(userId) ON DELETE CASCADE ON UPDATE CASCADE
);

-- InventoryItems table links to Books and Sellers
CREATE TABLE inventoryItems (
    inventoryItemId BIGINT PRIMARY KEY,
    inventoryItemUUID VARCHAR(255) NOT NULL UNIQUE,
    bookId BIGINT NOT NULL,
    sellerId BIGINT NOT NULL, -- Foreign key linking to the seller
    FOREIGN KEY (bookId) REFERENCES books(bookId) ON DELETE RESTRICT ON UPDATE CASCADE,
    FOREIGN KEY (sellerId) REFERENCES sellers(sellerId) ON DELETE RESTRICT ON UPDATE CASCADE -- New FK
);


CREATE TABLE orders (
    orderId BIGINT PRIMARY KEY,
    orderUUID VARCHAR(255) NOT NULL UNIQUE,
    buyerId BIGINT NOT NULL, -- Link order to a specific buyer
    date DATE,
    location VARCHAR(255), -- Shipping Address
    FOREIGN KEY (buyerId) REFERENCES buyers(buyerId) ON DELETE RESTRICT ON UPDATE CASCADE
);

CREATE TABLE orderItems (
    orderItemsId BIGINT PRIMARY KEY,
    orderId BIGINT NOT NULL,
    inventoryItemId BIGINT NOT NULL,
    quantity INT NOT NULL,
    FOREIGN KEY (orderId) REFERENCES orders(orderId) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (inventoryItemId) REFERENCES inventoryItems(inventoryItemId) ON DELETE RESTRICT ON UPDATE CASCADE
);
        
